    local function start_fn(inst)
        ------------------------------------------------------------------
        ---
            print("[TBAT][SNAIL]开始生成野生蘑菇蜗牛小屋区域")
        ------------------------------------------------------------------
        --- 先修正自己到地皮中心点
            local x,y,z = inst.Transform:GetWorldPosition()
            x,y,z = TBAT.MAP:GetTileCenterPoint(x,y,z)
            inst.Transform:SetPosition(x,y,z)
            local mid_pt = Vector3(x,0,z)
        ------------------------------------------------------------------
        --- 先删除区域内物品
            local mark_inst = CreateEntity()
            TheWorld.components.tbat_com_special_timer_for_theworld:AddTimer(mark_inst,0,function()
                mark_inst:Remove()                
                local function can_remove(target)
                    if target.sg or target.brainfn then
                        return false
                    end
                    if target:HasOneOfTags({"player","irreplaceable","nonpotatable","nosteal","antlion_sinkhole_blocker","event_trigger"}) then
                        return false
                    end
                    if target.components.container
                        or target.components.inventory
                        or target.components.inventoryitem
                        then
                            return false
                    end
                    return true
                end
                for x = -2, 3, 1 do
                    for z = -3, 3, 1 do
                        local offset_pt = Vector3(x*4,0,z*4)
                        local this_pt = mid_pt + offset_pt
                        local ents = TheSim:FindEntities(this_pt.x,this_pt.y,this_pt.z,3)
                        for k, target in pairs(ents) do
                            if can_remove(target) then
                                target:Remove()
                            end
                        end
                    end
                end
            end)
        ------------------------------------------------------------------
        --- 地皮创建
            local all_tile_data = json.decode(tile_data)
            for index, single_tile_data in pairs(all_tile_data) do
                local mark_inst = CreateEntity()
                TheWorld.components.tbat_com_special_timer_for_theworld:AddTimer(mark_inst,index*0.3,function()
                    mark_inst:Remove()
                    local offset_pt = Vector3(single_tile_data.pt.x,0,single_tile_data.pt.z)
                    local tile_name_index = single_tile_data.tile
                    local spawn_pt = mid_pt + offset_pt
                    TBAT.MAP:SetTileWithIndexAtPoint(spawn_pt.x,0,spawn_pt.z,tile_name_index)
                    print("[TBAT][SNAIL]野生蘑菇蜗牛小屋区域地皮生成中："..index.."/"..#all_tile_data)
                end)
            end
        ------------------------------------------------------------------
        --- 创建建筑
            local delay_time = #all_tile_data
            local mark_inst = CreateEntity()
            TheWorld.components.tbat_com_special_timer_for_theworld:AddTimer(mark_inst,delay_time*0.3 + 1,function()
                mark_inst:Remove()
                local all_data = json.decode(items_data)
                for k, data in pairs(all_data) do
                    local record = data.record
                    local offset_pt = Vector3(data.pt.x,0,data.pt.z)
                    local ret_pt = mid_pt + offset_pt
                    local this_tile = TBAT.MAP:GetTileIndexAtPoint(ret_pt.x,0,ret_pt.z)
                    if target_tiles[this_tile] or TheWorld.Map:IsAboveGroundAtPoint(ret_pt.x,0,ret_pt.z) then
                        SpawnSaveRecord(record).Transform:SetPosition(ret_pt.x,0,ret_pt.z)
                    end
                end
                print("[TBAT][SNAIL]野生蘑菇蜗牛小屋区域 生成完成")
            end)
        ------------------------------------------------------------------
        ---
            inst:Remove()
        ------------------------------------------------------------------
    end
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
--- 创建物品
    local function fn()
        local inst = CreateEntity()
        inst.entity:AddTransform()
        inst.entity:AddAnimState()
        inst.entity:AddSoundEmitter()
        inst.entity:AddNetwork()
        inst.AnimState:SetBank("cane")
        inst.AnimState:SetBuild("swap_cane")
        inst.AnimState:PlayAnimation("idle")
        inst:AddTag(this_prefab)
        inst.entity:SetPristine()
        inst.persists = false   --- 是否留存到下次存档加载。
        if not TheWorld.ismastersim then
            return inst
        end
        inst:AddComponent("inspectable")
        inst:AddComponent("named")
        inst.components.named:SetName("蜗牛创建区域")
        TheWorld.components.tbat_com_special_timer_for_theworld:AddOneTimeTimer(start_fn,inst)
        return inst
    end
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
return Prefab(this_prefab, fn, assets)
